<?phpclass Archivo {    public static function Subir() {        		//IMPLEMENTAR...OK        $tipoArchivo = pathinfo($_FILES["imagenASubir"]["name"], PATHINFO_EXTENSION);        date_default_timezone_set("America/Argentina/Buenos_Aires");		$destino = "./tmp/" . date("Ymd_His") . "." . $tipoArchivo;        $uploadOk = TRUE;        if (file_exists($destino))        {            $uploadOk = FALSE;            $mensaje = "El archivo ya existe. Verifique!!!";        }        if ($_FILES["imagenASubir"]["size"] > 500000)        {            $uploadOk = FALSE;            $mensaje = "El archivo es demasiado grande. Verifique!!!";        }        $esImagen = getimagesize($_FILES["imagenASubir"]["tmp_name"]);        if($esImagen === FALSE)        {            $uploadOk = FALSE;            $mensaje = "Sólo son permitidas IMAGENES.";        }        else        {            if($tipoArchivo != "jpg" && $tipoArchivo != "jpeg" && $tipoArchivo != "png")            {                $uploadOk = FALSE;                $mensaje = "Sólo son permitidas imágenes con extensión JPG, JPEG o PNG";            }        }        if ($uploadOk === FALSE)        {        }        else        {            if (move_uploaded_file($_FILES["imagenASubir"]["tmp_name"], $destino))            {                $mensaje = $destino;            }             else            {                $mensaje = "Lamentablemente ocurrió; un error y no se pudo subir el archivo.";            }        }        return $mensaje;    }    public static function Borrar($path) {		//IMPLEMENTAR...OK		return unlink($path);    }    public static function Mover($pathOrigen, $pathDestino) {		//IMPLEMENTAR...OK		return copy($pathOrigen, $pathDestino);    }}