<?php//IMPLEMENTAR...OKrequire_once ("./clases/AccesoDatos.php");require_once ("./clases/Usuario.php");session_start();if (isset($_POST["email"]) && isset($_POST["password"])){	$band = false;	$email = $_POST["email"];	$password = $_POST["password"];	$usuariosArray = Usuario::TraerTodosLosUsuarios();	foreach ($usuariosArray as $unUsuario)	{		if ($email == $unUsuario["email"] && $password == $unUsuario["password"])		{			$usuarioParaSesion = new stdClass();			$usuarioParaSesion->id = $unUsuario["id"];			$usuarioParaSesion->nombre = $unUsuario["nombre"];			$usuarioParaSesion->email = $unUsuario["email"];			$usuarioParaSesion->password = $unUsuario["password"];			$usuarioParaSesion->perfil = $unUsuario["perfil"];			$usuarioParaSesion->foto = $unUsuario["foto"];			$_SESSION["Usuario"] = json_encode($usuarioParaSesion);			$band = true;			echo "OK";			break;		}	}	if(!$band)	{		echo "NO_OK";	}}else if(!isset($_SESSION["Usuario"])){	header("location:login.php");}