<?php//IMPLEMENTAR...OKrequire_once ("./clases/AccesoDatos.php");require_once ("./clases/Usuario.php");session_start();if (isset($_POST["email"]) && isset($_POST["password"])){	$band = false;	$email = $_POST["email"];	$password = $_POST["password"];	$usuariosArray = Usuario::TraerTodosLosUsuarios();	foreach ($usuariosArray as $unUsuario)	{		if ($email == $unUsuario["email"] && $password == $unUsuario["password"])		{			$_SESSION["Usuario"] = json_encode($unUsuario);			$band = true;			echo "OK";			break;		}	}	if(!$band)	{		echo "NO_OK";	}}else if(!isset($_SESSION["Usuario"])){	header("location:login.php");}